package cap

import (
	"encoding/hex"
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestPacketFactory(t *testing.T) {
	pk := &PortKnocker{}
	hexstring := hex.EncodeToString(pkt)

	assert.Equal(t, len(""), len(hexstring))
	assert.Equal(t, "", hexstring)
}

